mapper.update:
UPDATE users
full_name = #{fullName}, phone_number = #{phoneNumber}, status = #{status}, updated_at = CURRENT_TIMESTAMP,
WHERE id = #{id}

mapper.select:
SELECT * FROM users
(LOWER(full_name) LIKE LOWER(CONCAT('%', #{search}, '%')) OR LOWER(email) LIKE LOWER(CONCAT('%', #{search}, '%')))
ORDER BY id DESC LIMIT #{limit} OFFSET #{offset}
